mapper.sql:
item_id as itemId, item_nm as itemNm, item_type as itemType, item_price as itemPrice, item_mtrl_cost as itemMtrlCost, item_person_cost as itemPersonCost, item_std01 as itemStd01, item_std02 as itemStd02, item_std03 as itemStd03, item_std04 as itemStd04, item_std05 as itemStd05, item_std_str01 as itemStdStr01, item_std_str02 as itemStdStr02, item_std_str03 as itemStdStr03, item_std_str04 as itemStdStr04, item_std_str05 as itemStdStr05, item_standard as itemStandard, item_unit as itemUnit, item_mtrl as itemMtrl, item_total_stock as itemTotalStock, description, is_use as isUse, created_at as createdAt, creator_id as creatorId, updated_at as updatedAt, updator_id as updatorId FROM bc_item_info WHERE 1=1
AND item_id = #{itemId}
AND item_type = #{itemType}
AND is_use = #{isUse}
AND item_id LIKE CONCAT('%',#{searchText},'%')
AND item_nm LIKE CONCAT('%',#{searchText},'%')

mapper.update:
UPDATE bc_item_info
SET updated_at = now()

		,item_nm = #{itemNm}


		,item_price = #{itemPrice}


		,item_mtrl_cost = #{itemMtrlCost}


		,item_person_cost = #{itemPersonCost}


		,item_type = #{itemType}


		,item_standard = #{itemStandard}


		,item_unit = #{itemUnit}


		,item_mtrl = #{itemMtrl}


		,item_total_stock = #{itemTotalStock}


		,description = #{description}


		,item_std01 = #{itemStd01}


		,item_std02 = #{itemStd02}


		,item_std03 = #{itemStd03}


		,item_std04 = #{itemStd04}


		,item_std05 = #{itemStd05}


		,item_std_str01 = #{itemStdStr01}


		,item_std_str02 = #{itemStdStr02}


		,item_std_str03 = #{itemStdStr03}


		,item_std_str04 = #{itemStdStr04}


		,item_std_str05 = #{itemStdStr05}


WHERE item_id = #{itemId}

mapper.insert:
INSERT INTO bc_rtl_item_comp
(
	item_id,
	comp_id
)
VALUES
(
	#{itemId},
	#{compId}
)

mapper.delete:
DELETE FROM bc_rtl_item_comp
WHERE 1=1
AND item_id = #{itemId}
AND comp_id = #{compId}

mapper.update:
UPDATE bc_item_info
SET is_use = #{isUse}
WHERE item_id = #{itemId}
