mapper.sql:
place.place_id AS placeId, place.place_nm AS placeNm, place.place_parent AS placeParent, place.place_manager AS placeManager, place.created_at AS createdAt, place.updated_at AS updatedAt, place.creator_id AS creatorId, place.updator_id AS updatorId, place.description AS DESCRIPTION, place.is_use AS isUse, place.place_std01 AS placeStd01, place.place_std02 AS placeStd02, place.place_std03 AS placeStd03, place.place_std04 AS placeStd04, place.place_std05 AS placeStd05, place.place_std_str01 AS placeStdStr01, place.place_std_str01 AS placeStdStr02, place.place_std_str01 AS placeStdStr03, place.place_std_str01 AS placeStdStr04, place.place_std_str01 AS placeStdStr05, urInfo.user_nm AS placeManagerNm, IF((SELECT COUNT(1)FROM bc_place_info child WHERE child.place_parent = place.place_id) > 0 ,TRUE,FALSE) AS hasChildren FROM bc_place_info place LEFT OUTER JOIN mb_user_info urInfo ON place.place_manager = urInfo.user_id WHERE 1=1
AND place_parent = #{placeParent}
AND ${searchGubun} LIKE CONCAT('%',#{searchText},'%')

mapper.update:
UPDATE bc_place_info
SET updator_id = #{updatorId},
	updated_at = NOW()

		,place_nm = #{placeNm}


		,place_manager = #{placeManager}


		,description = #{description}


		,is_use = #{isUse}


		,place_std01 = #{placeStd01}


		,place_std02 = #{placeStd02}


		,place_std03 = #{placeStd03}


		,place_std04 = #{placeStd04}


		,place_std05 = #{placeStd05}


		,place_std_str01 = #{placeStdStr01}


		,place_std_str02 = #{placeStdStr02}


		,place_std_str03 = #{placeStdStr03}


		,place_std_str04 = #{placeStdStr04}


		,place_std_str05 = #{placeStdStr05}

WHERE place_id = #{placeId}
