mapper.sql:
ur.user_id AS userId, ur.user_nm AS userNm, ur.is_lock AS isLock, ur.lang_cd AS langCd, ur.description AS description, dept.dept_id AS deptId, dept.dept_nm AS deptNm, pos.position_id AS positionId, pos.position_nm AS positionNm, ur.user_std01 AS userStd01, ur.user_std02 AS userStd02, ur.user_std03 AS userStd03, ur.user_std04 AS userStd04, ur.user_std05 AS userStd05, ur.user_std_str01 AS userStdStr01, ur.user_std_str02 AS userStdStr02, ur.user_std_str03 AS userStdStr03, ur.user_std_str04 AS userStdStr04, ur.user_std_str05 AS userStdStr05, ur.pw_change_at AS pwChangeAt FROM mb_user_info ur LEFT OUTER JOIN mb_position_info pos ON ur.position_id = pos.position_id AND pos.is_use = TRUE LEFT OUTER JOIN mb_dept_info dept ON ur.dept_id = dept.dept_id AND dept.is_use = TRUE WHERE ur.is_del = FALSE
AND ${searchGubun} LIKE CONCAT('%',#{searchText},'%')
AND ur.user_id = #{userId}

mapper.select:
SELECT * FROM (
		SELECT 


					ROW_NUMBER() OVER(ORDER BY ${sort}) AS RNUM,


					ROW_NUMBER() OVER() AS RNUM,



		)s_table
		WHERE RNUM  >=  #{firstIndex} AND RNUM  <=  #{lastIndex}

mapper.update:
UPDATE mb_user_info
SET updator_id = #{updatorId}, 
	updated_at = now()

		,password = #{password}


		,user_nm = #{userNm}


		,lang_cd = #{langCd}


		,fail_cnt = #{failCnt}


		,is_del = #{isDel}


		,is_lock = #{isLock}


		,description = #{description}


		,dept_id = #{deptId}


		,position_id = #{positionId}


		,user_std01 = #{userStd01}


		,user_std02 = #{userStd02}


		,user_std03 = #{userStd03}


		,user_std04 = #{userStd04}


		,user_std05 = #{userStd05}


		,user_std_str01 = #{userStdStr01}


		,user_std_str02 = #{userStdStr02}


		,user_std_str03 = #{userStdStr03}


		,user_std_str04 = #{userStdStr04}


		,user_std_str05 = #{userStdStr05}


		,pw_change_at = NOW()

WHERE user_id = #{userId}
