mapper.sql:
eqmtMgt.eqmt_mgt_id AS eqmtMgtId, eqmtMgt.eqmt_mgt_gbn AS eqmtMgtGbn, eqmtMgt.eqmt_mgt_mtnc AS eqmtMgtMtnc, eqmtMgt.eqmt_mgt_nm AS eqmtMgtNm, eqmtMgt.eqmt_mgt_purchase AS eqmtMgtPurchase, eqmtMgt.eqmt_mgt_verif AS eqmtMgtVerif, eqmtMgt.eqmt_mgt_desc AS eqmtMgtDesc, eqmtMgt.eqmt_mgt_file AS eqmtMgtFile, eqmtMgt.is_use AS isUse, eqmtMgt.creator_id AS creatorId, eqmtMgt.created_at AS createdAt, eqmtMgt.updator_id AS updatorId, eqmtMgt.updated_at AS updatedAt, eqmtMgt.place_id AS placeId, place.place_nm AS placeNm, eqmtMgt.comp_id AS compId, comp.comp_nm AS compNm, eqmtMgt.day_target AS dayTarget, eqmtMgt.eqmt_mgt_std01 AS eqmtMgtStd01, eqmtMgt.eqmt_mgt_std02 AS eqmtMgtStd02, eqmtMgt.eqmt_mgt_std03 AS eqmtMgtStd03, eqmtMgt.eqmt_mgt_std04 AS eqmtMgtStd04, eqmtMgt.eqmt_mgt_std05 AS eqmtMgtStd05, eqmtMgt.eqmt_mgt_std_str01 AS eqmtMgtStdStr01, eqmtMgt.eqmt_mgt_std_str02 AS eqmtMgtStdStr02, eqmtMgt.eqmt_mgt_std_str03 AS eqmtMgtStdStr03, eqmtMgt.eqmt_mgt_std_str04 AS eqmtMgtStdStr04, eqmtMgt.eqmt_mgt_std_str05 AS eqmtMgtStdStr05
FROM bc_equipment_mgt eqmtMgt LEFT OUTER JOIN bc_company_info comp ON comp.comp_id = eqmtMgt.comp_id LEFT OUTER JOIN bc_place_info place ON place.place_id = eqmtMgt.place_id WHERE 1=1
AND ${searchGubun} LIKE CONCAT('%',#{searchText},'%')
AND eqmtMgt.eqmt_mgt_id = #{eqmtMgtId}
AND eqmtMgt.is_use = #{isUse}

mapper.select:
SELECT * FROM (
		SELECT 


					ROW_NUMBER() OVER(ORDER BY ${sort}) AS RNUM,


					ROW_NUMBER() OVER(ORDER BY updatedAt desc) AS RNUM,



		)s_table
		WHERE RNUM  >=  #{firstIndex} AND RNUM  <=  #{lastIndex}

mapper.update:
UPDATE bc_equipment_mgt
SET updator_id = #{updatorId},	
	updated_at = NOW()


		,eqmt_mgt_nm = #{eqmtMgtNm}


		,eqmt_mgt_gbn = #{eqmtMgtGbn}


		,eqmt_mgt_mtnc = #{eqmtMgtMtnc}


		,eqmt_mgt_purchase = #{eqmtMgtPurchase}


		,eqmt_mgt_verif = #{eqmtMgtVerif}


		,eqmt_mgt_desc = #{eqmtMgtDesc}


		,eqmt_mgt_file = #{eqmtMgtFile}


		,eqmt_mgt_std01 = #{eqmtMgtStd01}


		,eqmt_mgt_std02 = #{eqmtMgtStd02}


		,eqmt_mgt_std03 = #{eqmtMgtStd03}


		,eqmt_mgt_std04 = #{eqmtMgtStd04}


		,eqmt_mgt_std05 = #{eqmtMgtStd05}


		,eqmt_mgt_std_str01 = #{eqmtMgtStdStr01}


		,eqmt_mgt_std_str02 = #{eqmtMgtStdStr02}


		,eqmt_mgt_std_str03 = #{eqmtMgtStdStr03}


		,eqmt_mgt_std_str04 = #{eqmtMgtStdStr04}


		,eqmt_mgt_std_str05 = #{eqmtMgtStdStr05}


		,is_use = #{isUse}


		,day_target = #{dayTarget}

WHERE eqmt_mgt_id  = #{eqmtMgtId}

mapper.insert:
INSERT INTO prs_rtl_tool_eqip
(
	eqmt_mgt_id,
	tool_id
)
VALUES
(
	#{eqmtMgtId}, 
	#{toolId}
)

mapper.delete:
DELETE FROM prs_rtl_tool_eqip
WHERE 1=1
AND eqmt_mgt_id  = #{eqmtMgtId}
AND tool_id = #{toolId}
