mapper.sql:
lot.lot_seq AS lotSeq, lot.lot_id AS lotId, lot.lot_nm AS lotNm, lot.lot_code AS lotCode, lot.lot_type AS lotType, lot.lot_qty AS lotQty, lot.lot_pm AS lotPm, lot.lot_state AS lotState, lot.lot_desc AS lotDesc, lot.created_at AS createdAt, lot.creator_id AS creatorId, lot.updated_at AS updatedAt, lot.updator_id AS updatorId, lot.item_id AS itemId, lot.order_id AS orderId, item.item_nm AS itemNm, item.item_deduction AS itemDeduction, urInfo.user_Nm AS lotPmNm, orInfo.order_nm AS orderNm, orInfo.order_manager AS orderManager, orInfo.order_stdt AS orderStdt, orInfo.order_eddt AS orderEddt, orInfo.order_cost AS orderCost, orInfo.comp_id AS compId, company.comp_nm AS compNm, orInfo.order_state AS orderState, orInfo.description AS description FROM prs_lot_info lot LEFT OUTER JOIN bc_item_info item on lot.item_id = item.item_id LEFT OUTER JOIN mb_user_info urInfo on lot.lot_pm = urInfo.user_id LEFT OUTER JOIN prs_order_info orInfo on orInfo.order_id = lot.order_id LEFT OUTER JOIN bc_company_info company on orInfo.comp_id = company.comp_id WHERE 1=1
AND lot.order_id = #{orderId}
AND lot.lot_id = #{lotId}
AND lot.lot_seq = #{lotSeq}
AND lot.lot_state = #{lotState}
AND lot.lot_type = #{lotType}
AND ${searchGubun} LIKE CONCAT('%',#{searchText},'%')

mapper.select:
SELECT * FROM (
	SELECT 


				ROW_NUMBER() OVER(ORDER BY ${sort}) AS RNUM,


				ROW_NUMBER() OVER(ORDER BY updatedAt desc) AS RNUM,



	)s_table

	WHERE RNUM  >=  #{firstIndex} AND RNUM  <=  #{lastIndex}

mapper.sql:
lot.order_id AS orderId, -- 수주ID odr.order_nm AS orderNm, -- 수져명 asgn.lot_id AS lotId, -- LotID lot.lot_nm AS lotNm, -- Lot명 lot.item_id AS itemId, -- 아이템ID item.item_nm AS itemNm, -- 아이템명 lot.lot_qty AS lotQty, -- 아이템 수주량 asgn.prod_asm_id AS prodAsmId, -- 작업지시 ID asgn.prod_asm_nm AS prodAsmNm, -- 작업지시명 asgn.prod_asm_date AS prodAsmDate, -- 작업지시일 asgn.prod_asm_qty AS prodAsmQty, -- 생산지시량 asgn.prod_asm_desc AS prodAsmDesc, -- 비고 asgn.prod_asm_user AS prodAsmUser, -- 작업지시 수신자 아이디(부서 담당자) d_usr.user_nm AS prodAsmUserNm, -- 작업지시 수신자 명 asgn.prod_asm_emj AS prodAsmEmj, -- 긴급여부 asgn.prod_asm_state AS prodAsmState, -- 작업상태 asgn.created_at AS createdAt, -- 생성일 asgn.creator_id AS creatorId, -- 생성자 asgn.dept_id AS deptId, -- 부서정보 d_dept.dept_Nm AS deptNm -- 부서명 FROM prs_product_assignment asgn LEFT OUTER JOIN mb_user_info d_usr ON d_usr.user_id = asgn.prod_asm_user -- 작업지시 수신자 정보 LEFT OUTER JOIN mb_dept_info d_dept ON d_dept.dept_id = asgn.dept_id -- 작업지시 수신자 부서 LEFT OUTER JOIN prs_lot_info lot ON lot.lot_id = asgn.lot_id -- Lot정보 LEFT OUTER JOIN prs_order_info odr ON odr.order_id = lot.order_id -- 수주정보 LEFT OUTER JOIN bc_item_info item ON item.item_id = lot.item_id -- 품목정보 WHERE 1=1
AND ${searchGubun} LIKE CONCAT('%',#{searchText},'%')
AND asgn.lot_id = #{lotId}
AND asgn.prod_asm_id = #{prodAsmId}
AND asgn.prod_asm_user = #{prodAsmUser}
AND asgn.prod_asm_emj = #{prodAsmEmj}
AND asgn.prod_asm_date = #{prodAsmDate}
AND asgn.prod_asm_state = #{prodAsmState}
AND asgn.dept_id = #{deptId}

lot.order_id AS orderId, -- 수주ID asgn.lot_id AS lotId, -- LotID lot.lot_nm AS lotNm, -- Lot명 item.item_id AS itemId, -- 아이템ID item.item_nm AS itemNm, -- 아이템명 asgn.prod_asm_id AS prodAsmId, -- 작업지시 ID asgn.prod_asm_nm AS prodAsmNm, -- 작업지시명 asgn.prod_asm_date AS prodAsmDate, -- 작업지시일 asgn.prod_asm_qty AS prodAsmQty, -- 생산지시량 asgn.prod_asm_desc AS prodAsmDesc, -- 비고 asgn.prod_asm_user AS prodAsmUser, -- 작업지시 수신자 아이디(부서 담당자) d_usr.user_nm AS prodAsmUserNm, -- 작업지시 수신자 명 asgn.prod_asm_emj AS prodAsmEmj, -- 긴급여부 asgn.prod_asm_state AS prodAsmState, -- 작업상태 asgn.created_at AS createdAt, -- 생성일 asgn.creator_id AS creatorId, -- 생성자 asgn.dept_id AS deptId, -- 부서정보 d_dept.dept_Nm AS deptNm -- 부서명 FROM prs_product_assignment asgn -- 생산지시 LEFT OUTER JOIN mb_user_info d_usr ON d_usr.user_id = asgn.prod_asm_user -- 작업지시 수신자 정보 LEFT OUTER JOIN mb_dept_info d_dept ON d_dept.dept_id = asgn.dept_id -- 작업지시 수신자 부서 LEFT OUTER JOIN prs_lot_info lot ON lot.lot_id = asgn.lot_id -- Lot정보 LEFT OUTER JOIN prs_order_info odr ON odr.order_id = lot.order_id -- 수주정보 LEFT OUTER JOIN bc_item_info item ON item.item_id = lot.item_id -- 품목정보 WHERE 1=1
AND ${searchGubun} LIKE CONCAT('%',#{searchText},'%')
AND asgn.prod_asm_id = #{prodAsmId}
AND asgn.prod_asm_user = #{prodAsmUser}
AND asgn.prod_asm_date = #{prodAsmDate}
AND asgn.prod_asm_emj = #{prodAsmEmj}
AND asgn.dept_id = #{deptId}

mapper.update:
UPDATE prs_product_assignment
SET prod_asm_id = prod_asm_id

		,prod_asm_nm = #{prodAsmNm}


		,prod_asm_qty = #{prodAsmQty}


		,prod_asm_desc = #{prodAsmDesc}


		,prod_asm_user = #{prodAsmUser}


		,prod_asm_emj = #{prodAsmEmj}


		,prod_asm_state = #{prodAsmState}


		,prod_asm_date = #{prodAsmDate}

WHERE prod_asm_id  = #{prodAsmId}

mapper.select:
SELECT rtl.eqmt_mgt_id AS eqmtMgtId, rtl.eqip_id AS eqipId, rtl.eqip_nm AS eqipNm, rtl.eqip_position AS eqipPosition, rtl.is_del AS isDel, rtl.tool_mgt_id AS toolMgtId, toolMgt.tool_mgt_purchase AS toolMgtPurchase, toolMgt.tool_mgt_state AS toolMgtState, toolMgt.tool_mgt_limit AS toolMgtLimit, toolMgt.tool_mgt_use AS toolMgtUse, toolMgt.tool_mgt_verif AS toolMgtVerif, toolMgt.tool_mgt_gbn AS toolMgtGbn, toolMgt.tool_mgt_desc AS toolMgtDesc, toolMgt.comp_id AS compId, comp.comp_nm AS compNm, toolMgt.tool_id AS toolId, toolInfo.tool_nm AS toolNm, routTool.tool_count AS toolCount FROM bc_rtl_equip_tool rtl LEFT OUTER JOIN cm_tool_mgt toolMgt ON toolMgt.tool_mgt_id = rtl.tool_mgt_id LEFT OUTER JOIN bc_tool_info toolInfo ON toolInfo.tool_id = toolMgt.tool_id LEFT OUTER JOIN bc_company_info comp ON comp.comp_id = toolMgt.comp_id LEFT OUTER JOIN bc_rtl_routing_tool routTool ON routTool.tool_id = toolInfo.tool_id WHERE 1=1 AND rtl.is_del = FALSE
AND rtl.eqmt_mgt_id = #{eqmtMgtId}
AND routTool.routing_id = #{routingId}
AND rtl.eqip_id = #{eqipId}
AND rtl.tool_mgt_id = #{toolMgtId}

SELECT lot.order_id AS orderId, -- 수주ID lot.lot_id AS lotId, -- LotID lot.lot_nm AS lotNm, -- Lot명 item.item_id AS itemId, -- 아이템ID item.item_nm AS itemNm, -- 아이템명 item.item_total_stock AS itemTotalStock, -- 총재고량 item.item_std01 AS itemStd01, -- 단위1 item.item_std02 AS itemStd02, -- 단위2 item.item_std03 AS itemStd03, -- 단위3 item.item_std04 AS itemStd04, -- 단위4 item.item_std05 AS itemStd05, -- 단위5 item.item_std_str01 AS itemStdStr01, item.item_std_str02 AS itemStdStr02, item.item_std_str03 AS itemStdStr03, item.item_std_str04 AS itemStdStr04, item.item_std_str05 AS itemStdStr05, item.item_unit AS itemUnit, -- 유닛 item.item_mtrl AS itemMtrl, -- 재질 asgn.prod_asm_id AS prodAsmId, -- 작업지시 ID asgn.prod_asm_nm AS prodAsmNm, -- 작업지시명 asgn.prod_asm_date AS prodAsmDate, -- 작업지시일 asgn.prod_asm_qty AS prodAsmQty, -- 생산지시량 asgn.prod_asm_desc AS prodAsmDesc, -- 비고 asgn.prod_asm_user AS prodAsmUser, -- 작업지시 수신자 아이디(부서 담당자) asgn.prod_asm_emj AS prodAsmEmj, -- 긴급여부 asgn.prod_asm_state AS prodAsmState, -- 작업상태 rut.routing_id AS routingId, -- 라우팅ID rut.routing_seq AS routingSeq, -- 라우팅 순서 rut.routing_type AS routingType, -- 공정정보 wok.eqmt_mgt_id AS eqmtMgtId, bom.bom_Id AS bomId, bom.bom_nm AS bomNm, bom.bom_target AS bomTarget, bom.bom_target_cnt AS bomTargetCnt, equip.eqmt_mgt_nm AS eqmtMgtNm, equip.place_id AS placeId, place.place_nm AS placeNm, wok.prod_work_id AS prodWorkId, -- 작업정보ID wok.prod_work_user AS prodWorkUser, -- 작업자 아이디 w_usr.user_nm AS prodWorkUserNm, -- 작업자 명 wok.prod_work_qty AS prodWorkQty, -- 작업자 작업지시량 wok.prod_work_good AS prodWorkGood, -- 생산량 wok.prod_work_bad AS prodWorkBad, -- 불량량 wok.prod_work_start AS prodWorkStart, -- 시작시간 wok.prod_work_end AS prodWorkEnd, -- 종료시간 wok.prod_work_status AS prodWorkStatus, -- 작업상태 wok.prod_work_desc AS prodWorkDesc -- 작업지시 비고 FROM prs_work_info wok LEFT OUTER JOIN prs_lot_info lot ON wok.lot_id = lot.lot_id LEFT OUTER JOIN bc_item_info item ON item.item_id = lot.item_id -- 품목정보 LEFT OUTER JOIN prs_product_assignment asgn ON wok.prod_asm_id = asgn.prod_asm_id -- 생산지시 LEFT OUTER JOIN bc_routing_info rut ON wok.routing_id = rut.routing_id -- 라우팅 정보 LEFT OUTER JOIN bc_rtl_bom_routing rtl ON rtl.routing_id = rut.routing_id -- routing - bom 매핑테이블 LEFT OUTER JOIN bc_bom_info bom ON bom.bom_id = rtl.bom_id -- bom 정보 LEFT OUTER JOIN bc_equipment_mgt equip ON wok.eqmt_mgt_id = equip.eqmt_mgt_id LEFT OUTER JOIN bc_place_info place ON equip.place_id = place.place_id LEFT OUTER JOIN mb_user_info w_usr ON w_usr.user_id = wok.prod_work_user -- 작업자 정보 WHERE 1=1
AND ${searchGubun} LIKE CONCAT('%',#{searchText},'%')
AND lot.lot_id = #{lotId}
AND asgn.prod_asm_id = #{prodAsmId}
AND asgn.prod_asm_emj = #{prodAsmEmj}
AND asgn.prod_asm_date = #{prodAsmDate}
AND asgn.dept_id = #{deptId}
AND wok.prod_work_id = #{prodWorkId}
AND wok.routing_id = #{routingId}
AND wok.prod_work_user = #{prodWorkUser}
ORDER BY ${sort}
ORDER BY lot.lot_id DESC, rut.routing_seq ASC

mapper.update:
UPDATE prs_work_info
SET prod_asm_id = prod_asm_id

		,routing_id = #{routingId}


		,eqmt_mgt_id = #{eqmtMgtId}


		,prod_work_user = #{prodWorkUser}


		,prod_work_qty = #{prodWorkQty}


		,prod_work_good = #{prodWorkGood}


		,prod_work_bad = #{prodWorkBad}


		,prod_work_start = #{prodWorkStart}


		,prod_work_end = #{prodWorkEnd}


		,prod_work_desc = #{prodWorkDesc}


		,prod_work_status = #{prodWorkStatus}

WHERE prod_work_id  = #{prodWorkId}

mapper.select:
SELECT bom.bom_id, bom.bom_nm, bom.bom_target, bom.bom_target_id, bom.bom_target_cnt, mtrl.mtrl_nm, mtrlMgt.mtrl_mgt_id FROM bc_bom_info bom LEFT OUTER JOIN bc_material_info mtrl ON bom.bom_target_id = mtrl.mtrl_id LEFT OUTER JOIN cm_mtrl_mgt mtrlMgt ON bom.bom_target_id = mtrlMgt.mtrl_id WHERE 1=1
AND bom_parent_id = (SELECT bom_id FROM bc_rtl_bom_routing WHERE routing_id = #{routingId})
AND bom_parent_id = #{bomId}

SELECT count(*)
FROM qm_mtrl_mgt
WHERE mtrl_mgt_id = #{mtrlMgtId} AND mtrl_qty_target = #{mtrlQtyTarget} AND mtrl_qty_target_code = #{mtrlQtyTargetCode}

SELECT count(*)
FROM qm_item_mgt
WHERE  item_id = #{itemId} 
	AND item_mgt_id = #{itemMgtId} 
	AND item_qty_target = 'prod_tagt01' 
	AND item_qty_target_code = #{itemQtyTargetCode}

SELECT bomId AS bomId, bomTargetId AS bomTargetId, mtrl.mtrl_id AS mtrlId, mtrl.mtrl_nm AS mtrlNm, mtrlMgt.mtrl_mgt_id AS mtrlMgtId, mtrl_mgt_cnt AS mtrlMgtCnt, rut.routing_id AS routingId, rut.routing_seq AS routingSeq, wrk.prod_work_id AS prodWorkId, SUM(mtrl_qty_total) AS mtrlQtyTotal FROM(
)bom LEFT OUTER JOIN bc_rtl_bom_routing rtl ON rtl.bom_id = bom.bomParentId -- routing - bom 매핑테이블 LEFT OUTER JOIN bc_routing_info rut ON rtl.routing_id = rut.routing_id -- 라우팅 정보 LEFT OUTER JOIN prs_work_info wrk ON wrk.routing_id = rut.routing_id AND wrk.prod_asm_id = #{prodAsmId} LEFT OUTER JOIN bc_material_info mtrl ON bom.bomTargetId = mtrl.mtrl_id LEFT OUTER JOIN cm_mtrl_mgt mtrlMgt ON mtrl.mtrl_id = mtrlMgt.mtrl_id LEFT OUTER JOIN qm_mtrl_mgt mtrlCnt ON mtrlMgt.mtrl_mgt_id = mtrlCnt.mtrl_mgt_id AND (mtrlCnt.mtrl_qty_target_code = wrk.prod_work_id OR mtrlCnt.mtrl_qty_target_code IN (SELECT bad_id FROM prs_bad_product WHERE bad_target = 'qual_spec02' AND bad_target_code = wrk.prod_work_id)) GROUP BY bomId
ORDER BY ${sort}
ORDER BY rut.routing_seq ASC

SELECT bad.bad_id AS badId, -- 불량아이디 bad.bad_code AS badCode, -- 불량코드(기초코드) bad.bad_qty AS badQty, -- 불량수량 bad.chk_user AS chkUser, -- 불량등록자 아이디 badUser.user_nm AS chkUserNm, -- 불량등록자 이름 bad.chk_date AS chkDate, -- 불략확인일 bad.bad_target AS badTarget, -- 불량업무구분(기초코드) bad.bad_target_code AS badTargetCode, -- 불량업무코드(대상ID매핑) '' AS badTargetNm, -- 작업정보에에는 명칭이 없으므로 생략 bad.bad_desc AS badDesc, -- 불량설명 mtrlMgt.mtrl_id AS mtrlId, -- 자재 ID mtrlInfo.mtrl_nm AS mtrlNm, -- 자재명 mtrlInfo.description AS mtrlDiv, -- 자재구분 mtrlQm.mtrl_mgt_id AS mtrlMgtId, -- 자재상세ID workInfo.prod_work_user AS badPgUser, -- 작업자 아이디 workUser.user_nm AS badPgUserNm, -- 작업자 이름 workInfo.prod_work_end AS badPgDate, -- 작업 종료 시간 workInfo.lot_id AS badPgCd, -- Lot코드 lot.lot_nm AS badPgNm -- Lot 명 FROM prs_bad_product bad JOIN qm_mtrl_mgt mtrlQm ON mtrlQm.mtrl_qty_target_code = bad.bad_id AND mtrlQm.mtrl_qty_target = 'mtrl_tagt03' -- 수량관리 JOIN prs_work_info workInfo ON workInfo.prod_work_id = bad.bad_target_code AND bad.bad_target = 'qual_spec02' -- 생산작업정보 LEFT OUTER JOIN prs_lot_info lot ON lot.lot_id = workInfo.lot_id -- Lot정보 LEFT OUTER JOIN cm_mtrl_mgt mtrlMgt ON mtrlMgt.mtrl_mgt_id = mtrlQm.mtrl_mgt_id -- 자재상세정보 확인 LEFT OUTER JOIN bc_material_info mtrlInfo ON mtrlInfo.mtrl_id = mtrlMgt.mtrl_id -- 자재마스터 확인 LEFT OUTER JOIN mb_user_info badUser ON badUser.user_id = bad.chk_user -- 불량등록자 확인 LEFT OUTER JOIN mb_user_info workUser ON workUser.user_id = workInfo.prod_work_user -- 수량 확인자 WHERE 1=1
AND ${searchGubun} LIKE CONCAT('%',#{searchText},'%')
AND mtrlQm.mtrl_mgt_id = #{mtrlMgtId}
AND mtrlMgt.mtrl_id = #{mtrlId}
AND bad.bad_id = #{badId}
AND bad.bad_target = #{badTarget}
AND bad.bad_target_code = #{badTargetCode}
AND badUser.user_nm = #{chkUserNm}
AND workUser.user_nm = #{badPgUserNm}
AND workInfo.lot_id = #{badPgCd}
AND lot.lot_nm = #{badPgNm}
