mapper.sql:
rlesPlan.rles_plan_id AS rlesPlanId, rlesPlan.rles_plan_qty AS rlesPlanQty, rlesPlan.rles_plan_date AS rlesPlanDate, rlesPlan.rles_plan_state AS rlesPlanState, rlesPlan.creator_id AS creatorId, rlesPlan.created_at AS createdAt, rlesPlan.updator_id AS updatorId, rlesPlan.updated_at AS updatedAt, lot.lot_id AS lotId, itemInfo.item_id AS itemId, itemInfo.item_nm AS itemNm, itemMgt.item_mgt_id AS itemMgtId, itemMgt.item_stock AS itemStock, rlesMgt.rles_mgt_id AS rlesMgtId, rlesMgt.rles_mgt_qty AS rlesMgtQty, rlesMgt.rles_mgt_desc AS rlesMgtDesc, rlesMgt.rles_work_start AS rlesWorkStart, rlesMgt.rles_work_end AS rlesWorkEnd FROM prs_rles_plan rlesPlan LEFT OUTER JOIN prs_lot_info lot ON rlesPlan.lot_id = lot.lot_id LEFT OUTER JOIN bc_item_info itemInfo ON lot.item_id = itemInfo.item_id LEFT OUTER JOIN cm_item_mgt itemMgt ON rlesPlan.lot_id = itemMgt.item_mgt_id LEFT OUTER JOIN prs_rles_mgt rlesMgt ON rlesPlan.rles_plan_id = rlesMgt.rles_plan_id WHERE rles_plan_state != "rlesSta03"
AND ${searchGubun} LIKE CONCAT('%',#{searchText},'%')
AND rlesPlan.rles_plan_id = #{rlesPlanId}

mapper.select:
SELECT * FROM (
		SELECT 


					ROW_NUMBER() OVER(ORDER BY ${sort}) AS RNUM,


					ROW_NUMBER() OVER(ORDER BY rlesPlanId desc) AS RNUM,



		)s_table
WHERE RNUM  >=  #{firstIndex} AND RNUM  <=  #{lastIndex}

mapper.update:
UPDATE prs_rles_mgt
SET updator_id = #{updatorId},	
	updated_at = NOW()

	,rles_mgt_qty = #{rlesMgtQty}

	,rles_work_end = #{rlesWorkEnd}

	,rles_worker = #{rlesWorker}


	,rles_mgt_desc = #{rlesMgtDesc}

WHERE 1=1 
AND rles_mgt_id = #{rlesMgtId}

UPDATE cm_item_mgt
SET item_package_stock = item_package_stock + #{rlesMgtQty} 
WHERE 1=1
AND item_id = #{itemId} 
AND item_mgt_id = #{lotId}
