mapper.sql:
shipPlan.ship_plan_id AS shipPlanId, shipPlan.ship_plan_qty AS shipPlanQty, shipPlan.ship_plan_date AS shipPlanDate, shipPlan.ship_plan_state AS shipPlanState, shipPlan.ship_plan_desc AS shipPlanDesc, shipPlan.lot_id AS lotId, shipPlan.creator_id AS creatorId, shipPlan.created_at AS createdAt, shipPlan.updator_id AS updatorId, shipPlan.updated_at AS updatedAt, lot.lot_state AS lotState, orderInfo.order_id AS orderId, orderInfo.comp_id AS compId, comp.comp_nm AS compNm, itemInfo.item_id AS itemId, itemInfo.item_nm AS itemNm, userInfo.user_id AS userId, userInfo.user_nm AS userNm, (lotRlesQty.rlesWorkQty-IFNULL(lotShipQty.shipEndQty,0)) AS rlesStockQty, shipMgt.ship_id AS shipId, shipMgt.ship_qty AS shipQty, shipMgt.ship_complete_date AS shipCompleteDate, shipMgt.ship_desc AS shipDesc, shipMgt.ship_transport AS shipTransport, shipMgt.ship_worker AS shipWorker, shipMgt.ship_inspect AS shipInspect FROM prs_ship_plan shipPlan LEFT OUTER JOIN prs_ship_mgt shipMgt ON shipPlan.ship_plan_id = shipMgt.ship_plan_id LEFT OUTER JOIN prs_lot_info lot ON shipPlan.lot_id = lot.lot_id LEFT OUTER JOIN bc_item_info itemInfo ON lot.item_id = itemInfo.item_id LEFT OUTER JOIN mb_user_info userInfo ON shipMgt.ship_worker = userInfo.user_id LEFT OUTER JOIN prs_order_info orderInfo ON lot.order_id = orderInfo.order_id LEFT OUTER JOIN bc_company_info comp ON orderInfo.comp_id = comp.comp_id LEFT OUTER JOIN ( SELECT lot.lot_id AS lotId, SUM(rlesMgt.rles_mgt_qty) AS rlesWorkQty FROM prs_lot_info lot
LEFT OUTER JOIN prs_rles_plan rlesPlan ON lot.lot_id = rlesPlan.lot_id LEFT OUTER JOIN prs_rles_mgt rlesMgt ON rlesPlan.rles_plan_id = rlesMgt.rles_plan_id WHERE rlesPlan.rles_plan_state = 'rlesSta03' GROUP BY lot.lot_id )lotRlesQty ON lot.lot_id = lotRlesQty.lotId LEFT OUTER JOIN ( SELECT lot.lot_id AS lotId, SUM(shipMgt.ship_qty) AS shipEndQty FROM prs_lot_info lot
LEFT OUTER JOIN prs_ship_plan shipPlan ON lot.lot_id = shipPlan.lot_id LEFT OUTER JOIN prs_ship_mgt shipMgt ON shipPlan.ship_plan_id = shipMgt.ship_plan_id WHERE shipPlan.ship_plan_state = 'shipSta03' GROUP BY lot.lot_id )lotShipQty ON lot.lot_id = lotShipQty.lotId WHERE 1=1
AND shipPlan.ship_plan_id = #{shipPlanId}
AND shipPlan.ship_plan_state != #{shipPlanState}
AND shipPlan.lot_id = #{lotId}
AND ${searchGubun} LIKE CONCAT('%',#{searchText},'%')

mapper.select:
SELECT * FROM (
		SELECT 


					ROW_NUMBER() OVER(ORDER BY ${sort}) AS RNUM,


					ROW_NUMBER() OVER(ORDER BY shipPlan.updated_at desc) AS RNUM,



		)s_table
WHERE RNUM  >=  #{firstIndex}

mapper.update:
UPDATE prs_ship_mgt
SET updator_id = #{updatorId},	
	updated_at = NOW()

		,ship_qty = #{shipQty}


		,ship_desc = #{shipDesc}


		,ship_complete_date = #{shipCompleteDate}


		,ship_inspect = #{shipInspect}

WHERE 1=1

		AND ship_id = #{shipId}


		AND lot_id = #{lotId}

UPDATE prs_lot_info
SET lot_state = #{lotState}
WHERE lot_id = #{lotId}

UPDATE prs_ship_mgt
SET updator_id = #{updatorId},	
	updated_at = NOW() ,
	ship_inspect = #{shipInspect}
WHERE ship_id = #{shipId}
