mapper.sql:
shipPlan.ship_plan_id AS shipPlanId, shipPlan.ship_plan_qty AS shipPlanQty, shipPlan.ship_plan_date AS shipPlanDate, shipPlan.ship_plan_desc AS shipPlanDesc, shipPlan.ship_plan_state AS shipPlanState, shipPlan.creator_id AS creatorId, shipPlan.created_at AS createdAt, shipPlan.updator_id AS updatorId, shipPlan.updated_at AS updatedAt, lot.lot_id AS lotId, itemInfo.item_id AS itemId, rlesPlanTotal.plan_total AS rlesPlanTotal, itemInfo.item_nm AS itemNm FROM prs_ship_plan shipPlan LEFT OUTER JOIN prs_lot_info lot ON shipPlan.lot_id = lot.lot_id LEFT OUTER JOIN bc_item_info itemInfo ON lot.item_id = itemInfo.item_id LEFT OUTER JOIN ( SELECT lot_id, SUM(rles_plan_qty) AS plan_total FROM prs_rles_plan GROUP BY lot_id )rlesPlanTotal ON lot.lot_id = rlesPlanTotal.lot_id
WHERE shipPlan.lot_id = #{lotId}
AND ${searchGubun} LIKE CONCAT('%',#{searchText},'%')
AND shipPlan.ship_plan_id = #{shipPlanId}
AND shipPlan.ship_plan_state != #{shipPlanState}

mapper.select:
SELECT * FROM (
		SELECT 


					ROW_NUMBER() OVER(ORDER BY ${sort}) AS RNUM,


					ROW_NUMBER() OVER(ORDER BY updatedAt desc) AS RNUM,



		)s_table
		WHERE RNUM  >=  #{firstIndex}

SELECT * FROM (
		SELECT 


					ROW_NUMBER() OVER(ORDER BY ${sort}) AS RNUM,


					ROW_NUMBER() OVER(ORDER BY updatedAt desc) AS RNUM,



		)s_table
		WHERE RNUM  >=  #{firstIndex}

mapper.insert:
SELECT CREATE_PK('SHIPPLN') AS shipPlanId
INSERT INTO prs_ship_plan( ship_plan_id, ship_plan_qty, ship_plan_date, ship_plan_state, ship_plan_desc, lot_id, creator_id, created_at, updator_id, updated_at
)VALUES( #{shipPlanId}, #{shipPlanQty}, #{shipPlanDate}, 'shipSta01', #{shipPlanDesc}, #{lotId}, #{creatorId}, NOW(), #{updatorId}, NOW() )

mapper.update:
UPDATE prs_ship_plan
SET updator_id = #{updatorId},	
	updated_at = NOW()

	,ship_plan_date = #{shipPlanDate}


	,ship_plan_qty = #{shipPlanQty}


	,ship_plan_state = #{shipPlanState}


	,ship_plan_desc = #{shipPlanDesc}

WHERE 1=1
AND ship_plan_id = #{shipPlanId}
