odr.order_id AS orderId, odr.order_nm AS orderNm, odr.order_stdt AS orderStdt, lot.lot_id AS lotId, lot.lot_nm AS lotNm, lot.lot_qty AS lotQty, item.item_id AS itemId, item.item_nm AS itemNm, rout.routing_id AS routingId, workBase.routing_type AS routingType, workBase.routing_seq AS routingSeq, workBase.prod_work_id AS pordWorkId, workBase.prod_work_user AS prodWorkUser, workBase.user_nm AS prodWorkUserNm, workBase.prod_work_start AS prodWorkStart, workBase.prod_work_end AS prodWorkEnd, workBase.prod_work_qty AS prodWorkQty, workBase.prod_work_good AS prodWorkGood, workBase.prod_work_bad AS prodWorkBad, workBase.eqmt_mgt_id AS eqmtMgtId, workBase.eqmt_mgt_nm AS eqmtMgtNm, workBase.place_id AS placeId, workBase.place_nm AS placeNm, IF(IFNULL(SUM(rout.man_hour * workBase.prod_work_qty),0) > 0,SUM(rout.man_hour * workBase.prod_work_qty)/60,0) AS baseHour, IF(IFNULL(workBase.realTime,0) > 0,workBase.realTime/60,0 )AS realTime FROM prs_lot_info lot LEFT OUTER JOIN prs_order_info odr ON lot.order_id = odr.order_id LEFT OUTER JOIN bc_item_info item ON lot.item_id = item.item_id LEFT OUTER JOIN bc_routing_info rout ON rout.item_id = item.item_id LEFT OUTER JOIN (SELECT wok.routing_id, rout.routing_type, rout.routing_seq, wok.prod_work_id, wok.prod_work_user, usr.user_nm, wok.prod_work_start, wok.prod_work_end, wok.prod_work_qty, wok.prod_work_good, wok.prod_work_bad, wok.eqmt_mgt_id, eqmt.eqmt_mgt_nm, eqmt.place_id, place.place_nm, TIMESTAMPDIFF(SECOND,prod_work_start ,prod_work_end) AS realTime FROM prs_work_info wok LEFT OUTER JOIN bc_routing_info AS rout ON wok.routing_id = rout.routing_id LEFT OUTER JOIN mb_user_info AS usr ON wok.prod_work_user = usr.user_id LEFT OUTER JOIN bc_equipment_mgt eqmt ON eqmt.eqmt_mgt_id = wok.eqmt_mgt_id LEFT OUTER JOIN bc_place_info place ON eqmt.place_id = place.place_id WHERE 1=1
AND lot_id = #{lotId}
)AS workBase ON workBase.routing_id = rout.routing_id WHERE 1=1
AND ${searchGubun} LIKE CONCAT('%',#{searchText},'%')
AND lot.lot_id = #{lotId}
