 불량등록자 이름 bad.chk_date AS chkDate, -- 불략확인일 bad.bad_target AS badTarget, -- 불량업무구분(기초코드) bad.bad_target_code AS badTargetCode, -- 불량업무코드(대상ID매핑) '' AS badTargetNm, -- 작업정보에에는 명칭이 없으므로 생략 bad.bad_desc AS badDesc, -- 불량설명 mtrlMgt.mtrl_id AS mtrlId, -- 자재 ID mtrlInfo.mtrl_nm AS mtrlNm, -- 자재명 mtrlInfo.description AS mtrlDiv, -- 자재구분 mtrlQm.mtrl_mgt_id AS mtrlMgtId, -- 자재상세ID workInfo.prod_work_user AS badPgUser, -- 작업자 아이디 workUser.user_nm AS badPgUserNm, -- 작업자 이름 workInfo.prod_work_end AS badPgDate, -- 작업 종료 시간 workInfo.lot_id AS badPgCd, -- Lot코드 lot.lot_nm AS badPgNm -- Lot 명 FROM prs_bad_product bad JOIN qm_mtrl_mgt mtrlQm ON mtrlQm.mtrl_qty_target_code = bad.bad_id AND mtrlQm.mtrl_qty_target = 'mtrl_tagt03' -- 수량관리 JOIN prs_mtrl_useinfo useInfo ON useInfo.mtrl_use_id = bad.bad_target_code AND bad.bad_target = 'qual_spec02' -- 자재사용정보 LEFT OUTER JOIN prs_work_info workInfo ON workInfo.prod_work_id = useInfo.prod_work_id -- 생산작업정보 LEFT OUTER JOIN prs_lot_info lot ON lot.lot_id = workInfo.lot_id -- Lot정보 LEFT OUTER JOIN cm_mtrl_mgt mtrlMgt ON mtrlMgt.mtrl_mgt_id = mtrlQm.mtrl_mgt_id -- 자재상세정보 확인 LEFT OUTER JOIN bc_material_info mtrlInfo ON mtrlInfo.mtrl_id = mtrlMgt.mtrl_id -- 자재마스터 확인 LEFT OUTER JOIN mb_user_info badUser ON badUser.user_id = bad.chk_user -- 불량등록자 확인 LEFT OUTER JOIN mb_user_info workUser ON workUser.user_id = workInfo.prod_work_user -- 수량 확인자 )u_table WHERE 1=1
