-- UNION -- 3. 품목 - 클레임 불량 -- SELECT -- bad.bad_id AS badId, -- 불량아이디 -- bad.lot_id AS lotId, -- Lot ID -- bad.bad_code AS badCode, -- 불량코드(기초코드) -- bad.bad_qty AS badQty, -- 불량수량 -- bad.chk_user AS chkUser, -- 불량등록자 아이디 -- chkUserInfo.user_nm AS chkUserNm, -- 불량등록자 이름 -- bad.chk_date AS chkDate, -- 불략확인일 -- bad.bad_target AS badTarget, -- 불량업무구분(기초코드) -- bad.bad_target_code AS badTargetCode, -- 불량업무코드(대상ID매핑) -- claim.claim_nm AS badTargetNm, -- 작업정보에에는 명칭이 없으므로 생략 -- bad.bad_desc AS badDesc, -- 불량설명 -- itemInfo.item_id AS itemId, -- 품목ID -- itemInfo.item_nm AS itemNm, -- 품목 명 -- bad.chk_user AS badPgUser, -- 작업자 아이디 -- chkUserInfo.user_nm AS badPgUserNm, -- 작업자 이름 -- bad.chk_date AS badPgDate -- 작업 종료 시간
UNION -- 4. 품목 - 꽌리자 입력 SELECT DISTINCT bad.bad_id AS badId, -- 불량아이디 bad.lot_id AS lotId, -- Lot ID bad.bad_code AS badCode, -- 불량코드(기초코드) bad.bad_qty AS badQty, -- 불량수량 bad.chk_user AS chkUser, -- 불량등록자 아이디 chkUserInfo.user_nm AS chkUserNm, -- 불량등록자 이름 bad.chk_date AS chkDate, -- 불략확인일 bad.bad_target AS badTarget, -- 불량업무구분(기초코드) bad.bad_target_code AS badTargetCode, -- 불량업무코드(대상ID매핑) '' AS badTargetNm, -- 작업정보에에는 명칭이 없으므로 생략 bad.bad_desc AS badDesc, -- 불량설명 itemInfo.item_id AS itemId, -- 품목ID itemInfo.item_nm AS itemNm, -- 품목 명 '' AS badPgUser, -- 작업자 아이디 '' AS badPgUserNm, -- 작업자 이름 '' AS badPgDate -- 작업 종료 시간 FROM prs_bad_product bad INNER JOIN prs_lot_info lotInfo ON bad.lot_id = lotInfo.lot_id INNER JOIN bc_item_info itemInfo ON lotInfo.item_id = itemInfo.item_id INNER JOIN mb_user_info chkUserInfo ON bad.chk_user = chkUserInfo.user_id WHERE bad.bad_target_code = 'ADMIN' UNION -- 5. 품목 최종 검사 SELECT DISTINCT bad.bad_id AS badId, bad.lot_id AS lotId, bad.bad_code AS badCode, bad.bad_qty AS badQty, bad.chk_user AS chkUser, chkUserInfo.user_nm AS chkUserNm, bad.chk_date AS chkDate, bad.bad_target AS badTarget, bad.bad_target_code AS badTargetCode, '' AS badTargetNm, bad.bad_desc AS badDesc, itemInfo.item_id AS itemId, itemInfo.item_nm AS itemNm, '' AS badPgUser, '' AS badPgUserNm, '' AS badPgDate FROM prs_bad_product bad INNER JOIN prs_lot_info lotInfo ON bad.lot_id = lotInfo.lot_id INNER JOIN bc_item_info itemInfo ON lotInfo.item_id = itemInfo.item_id INNER JOIN mb_user_info chkUserInfo ON bad.chk_user = chkUserInfo.user_id WHERE bad.bad_target_code = 'FINAL' )u_table WHERE 1=1
