mapper.select:
SELECT attach_id AS attachId, attach_target AS attachTarget, attach_code AS attachCode, attach_path AS attachPath, attach_origin AS attachOrigin, attach_filename AS attachFilename, attach_size AS attachSize, attach_ext AS attachExt, img_yn AS imgYn, img_width AS imgWidth, img_height AS imgHeight, creator_id AS creatorId, created_at AS createdAt FROM cw_attach_mgt WHERE 1=1
AND attach_id = #{attachId}
AND attach_target = #{attachTarget}
AND attach_code = #{attachCode}
