mapper.sql:
orInfo.order_id AS orderId, orInfo.order_nm AS orderNm, orInfo.order_manager AS orderManager, orInfo.order_stdt AS orderStdt, orInfo.order_eddt AS orderEddt, orInfo.order_cost AS orderCost, orInfo.comp_id AS compId, orInfo.order_state AS orderState, orInfo.description AS description, orInfo.created_at AS createdAt, orInfo.creator_id AS creatorId, orInfo.updated_at AS updatedAt, orInfo.updator_id AS updatorId, urInfo.user_nm AS orderManagerNm, compInfo.comp_nm AS compNm, compInfo.comp_initials AS compInitials FROM prs_order_info orInfo LEFT OUTER JOIN mb_user_info urInfo ON orInfo.order_manager = urInfo.user_id LEFT OUTER JOIN bc_company_info compInfo ON orInfo.comp_id = compInfo.comp_id WHERE 1=1
AND ${searchGubun} LIKE CONCAT('%',#{searchText},'%')
AND orInfo.order_id = #{orderId}

mapper.select:
SELECT * FROM (
	SELECT 


				ROW_NUMBER() OVER(ORDER BY ${sort}) AS RNUM,


				ROW_NUMBER() OVER(ORDER BY updatedAt desc) AS RNUM,



	)s_table

		WHERE RNUM  >=  #{firstIndex} AND RNUM  <=  #{lastIndex}

mapper.update:
UPDATE prs_order_info
SET updator_id = #{updatorId},	
	updated_at = NOW()

		,order_nm = #{orderNm}


		,order_manager = #{orderManager}


		,order_stdt = #{orderStdt}


		,order_eddt = #{orderEddt}


		,order_cost = #{orderCost}


		,description = #{description}


		,order_state = #{orderState}


		,comp_id = #{compId}

WHERE order_id  = #{orderId}

mapper.sql:
lot.lot_seq AS lotSeq, lot.lot_id AS lotId, lot.lot_nm AS lotNm, lot.lot_code AS lotCode, lot.lot_type AS lotType, lot.lot_qty AS lotQty, IFNULL(lot_person_cost,0) AS lotPersonCost, IFNULL(lot_person_cost_after,0) AS lotPersonCostAfter, IFNULL(lot_mtrl_cost,0) AS lotMtrlCost, IFNULL(lot_mtrl_cost_after,0) AS lotMtrlCostAfter, lot.lot_pm AS lotPm, lot.lot_state AS lotState, lot.lot_desc AS lotDesc, lot.created_at AS createdAt, lot.creator_id AS creatorId, lot.updated_at AS updatedAt, lot.updator_id AS updatorId, lot.item_id AS itemId, -- lot.claim_id AS cliamId, lot.order_id AS orderId, lot.mtrl_id AS mtrlId, lot.mtrl_qty AS mtrlQty, mtrlMgt.mtrl_mgt_id AS mtrlMgtId, mtrlInfo.mtrl_nm AS mtrlNm, mtrlInfo.description AS mtrlDiv, mtrlInfo.mtrl_desc AS mtrlDesc, item.item_nm AS itemNm, item.item_deduction AS itemDeduction, urInfo.user_Nm AS lotPmNm,
orInfo.order_nm AS orderNm, orInfo.order_manager AS orderManager, orInfo.order_stdt AS orderStdt, orInfo.order_eddt AS orderEddt, orInfo.order_cost AS orderCost, orInfo.comp_id AS compId, orInfo.order_state AS orderState, orInfo.description AS description FROM prs_lot_info lot LEFT OUTER JOIN bc_item_info item on lot.item_id = item.item_id LEFT OUTER JOIN mb_user_info urInfo on lot.lot_pm = urInfo.user_id LEFT OUTER JOIN prs_order_info orInfo on orInfo.order_id = lot.order_id LEFT OUTER JOIN cm_mtrl_mgt mtrlMgt on mtrlMgt.lot_id = lot.lot_id LEFT OUTER JOIN bc_material_info mtrlInfo on mtrlInfo.mtrl_id = mtrlMgt.mtrl_id WHERE 1=1
AND lot.order_id = #{orderId}
AND lot.lot_seq = #{lotSeq}
AND lot.lot_state = #{lotState}
AND lot.lot_type = #{lotType}
AND ${searchGubun} LIKE CONCAT('%',#{searchText},'%')

mapper.select:
SELECT * FROM (
	SELECT 


				ROW_NUMBER() OVER(ORDER BY ${sort}) AS RNUM,


				ROW_NUMBER() OVER(ORDER BY updatedAt desc) AS RNUM,



	)s_table

		WHERE RNUM  >=  #{firstIndex} AND RNUM  <=  #{lastIndex}

mapper.update:
UPDATE prs_lot_info
SET updator_id = #{updatorId},	
	updated_at = NOW()

		,lot_nm = #{lotNm}


		,lot_code = #{lotCode}


		,lot_type = #{lotType}


		,lot_qty = #{lotQty}


		,lot_mtrl_cost = #{lotMtrlCost}


		,lot_mtrl_cost_after = #{lotMtrlCostAfter}


		,lot_person_cost = #{lotPersonCost}


		,lot_person_cost_after = #{lotPersonCostAfter}


		,lot_pm = #{lotPm}


		,lot_state = #{lotState}


		,lot_desc = #{lotDesc}


		,item_id = #{itemId}



		,mtrl_qty = #{mtrlQty}

WHERE lot_seq  = #{lotSeq}
