mapper.sql:
badCode, badQty, totalQty, badQty/(totalQty-badQty) AS totalBadRate FROM( SELECT MAX(bc.cd_id) AS badCode, SUM(bad.bad_qty) AS badQty, (SELECT SUM(bad_qty) AS totalQty FROM prs_bad_product WHERE 1=1
AND chk_date between #{searchStartDate} AND DATE_ADD(#{searchEndDate},INTERVAL 1 DAY)
) AS totalQty FROM cw_comn_code bc LEFT OUTER JOIN prs_bad_product bad ON bc.cd_id = bad.bad_code WHERE up_cd_id = #{badCode}
AND chk_date between #{searchStartDate} AND DATE_ADD(#{searchEndDate},INTERVAL 1 DAY)
GROUP BY cd_id )bad_table

mapper.select:
SELECT * FROM (
