	)g_table
)s_table

	WHERE RNUM  >=  #{firstIndex} AND RNUM  <=  #{lastIndex}

mapper.sql:
bad.bad_id AS badId, -- 불량아이디 bad.bad_code AS badCode, -- 불량코드(기초코드) bad.bad_qty AS badQty, -- 불량수량 bad.chk_user AS chkUser, -- 불량등록자 아이디 badUser.user_nm AS chkUserNm, -- 불량등록자 이름 bad.chk_date AS chkDate, -- 불략확인일 bad.bad_target AS badTarget, -- 불량업무구분(기초코드) bad.bad_target_code AS badTargetCode, -- 불량업무코드(대상ID매핑) bad.bad_desc AS badDesc -- 불량설명 FROM prs_bad_product bad LEFT OUTER JOIN mb_user_info badUser on bad.chk_user = badUser.user_id WHERE bad.bad_code = #{badCode}
AND chk_date between #{searchStartDate} AND DATE_ADD(#{searchEndDate},INTERVAL 1 DAY)
AND ${searchGubun} LIKE CONCAT('%',#{searchText},'%')

