mapper.sql:
place.place_id AS placeId, place.place_nm AS placeNm, place.place_parent AS placeParent, place.place_manager AS placeManager, place.place_seq AS placeSeq, place.place_std01 AS placeStd01, place.place_std02 AS placeStd02, place.place_str_std01 AS placeStrStd01, place.place_str_std02 AS placeStrStd02, place.created_at AS createdAt, place.updated_at AS updatedAt, place.creator_id AS creatorId, place.updator_id AS updatorId, place.description AS DESCRIPTION, place.is_use AS isUse, urInfo.user_nm AS placeManagerNm, IF((SELECT COUNT(1)FROM bc_place_info child WHERE child.place_parent = place.place_id) > 0 ,TRUE,FALSE) AS hasChildren FROM bc_place_info place LEFT OUTER JOIN mb_user_info urInfo ON place.place_manager = urInfo.user_id WHERE 1=1
AND ${searchGubun} LIKE CONCAT('%',#{searchText},'%')
AND place_str_std01 = #{placeStrStd01}
AND place_str_std02 = #{placeStrStd02}
AND place_parent = #{placeParent}

mapper.update:
UPDATE bc_place_info
SET updator_id = #{updatorId},	
	updated_at = NOW()

		,place_nm = #{placeNm}


		,place_manager = #{placeManager}


		,description = #{description}


		,place_seq = #{placeSeq}


		,place_std01 = #{placeStd01}


		,place_std02 = #{placeStd02}


		,place_str_std01 = #{placeStrStd01}


		,place_str_std02 = #{placeStrStd02}


		,is_use = #{isUse}

WHERE place_id = #{placeId}
