mapper.sql:
item_id as itemId, item_nm as itemNm, item_type as itemType, item_price as itemPrice, item_mtrl_cost as itemMtrlCost, item_person_cost as itemPersonCost, item_std01 as itemStd01, item_std02 as itemStd02, item_std03 as itemStd03, item_std04 as itemStd04, item_std05 as itemStd05, item_unit as itemUnit, item_mtrl as itemMtrl, item_total_stock as itemTotalStock, item_day_target as itemDayTarget, description, is_use as isUse, created_at as createdAt, creator_id as creatorId, updated_at as updatedAt, updator_id as updatorId, item_deduction as itemDeduction FROM bc_item_info WHERE 1=1
AND item_id = #{itemId}
AND item_type = #{itemType}
AND is_use = #{isUse}
AND item_id LIKE CONCAT('%',#{searchText},'%')
AND item_nm LIKE CONCAT('%',#{searchText},'%')

mapper.select:
SELECT * FROM ( SELECT
ROW_NUMBER() OVER(ORDER BY ${sort}) AS RNUM,
ROW_NUMBER() OVER(ORDER BY updatedAt DESC) AS RNUM,
)s_table
WHERE RNUM <= #{lastIndex} AND RNUM >= #{firstIndex}

mapper.update:
UPDATE bc_item_info SET item_nm = #{itemNm}, item_price = #{itemPrice}, item_mtrl_cost = #{itemMtrlCost}, item_person_cost = #{itemPersonCost}, item_type = #{itemType}, item_std01 = #{itemStd01}, item_std02 = #{itemStd02}, item_std03 = #{itemStd03}, item_std04 = #{itemStd04}, item_std05 = #{itemStd05}, item_unit = #{itemUnit}, item_mtrl = #{itemMtrl}, item_total_stock = #{itemTotalStock}, item_day_target = #{itemDayTarget}, description = #{description}, updated_at = now(), updator_id = #{updatorId}, item_deduction = #{itemDeduction}
,item_total_stock = #{itemTotalStock}
,item_day_target = #{itemDayTarget}
,item_std05 = #{itemStd05}
WHERE item_id = #{itemId}

UPDATE bc_item_info
SET
	item_deduction = #{itemDeduction}
WHERE item_id = #{itemId}

mapper.insert:
INSERT INTO bc_rtl_item_comp
(
	item_id,
	comp_id
)
VALUES
(
	#{itemId},
	#{compId}
)

mapper.delete:
DELETE FROM bc_rtl_item_comp
WHERE 1=1
AND item_id = #{itemId}
AND comp_id = #{compId}

mapper.update:
UPDATE bc_item_info
SET is_use = #{isUse}
WHERE item_id = #{itemId}
